import * as types from '@/store/mutation-types.js';
import http from '@/fetch/http.js';
import router from '@/router';
export default {
  name: 'header_box',
  data: function data() {
    return {};
  },
  created: function created() {
    this.init();
  },
  methods: {
    init: function init() {
      var self = this;
      var url = "/common/curuser/";
      http.get(url).then(function (res) {
        if (res.response == 'expired') {
          self.$store.commit(types.LOGOUT);
          window.location.href = '/logout/';
        } else if (res.user && res.user.id) {
          self.$store.commit(types.LOGIN, {
            'user': res.user
          });
          self.$i18n.locale = res.user.language;
          router.push({
            'name': 'main'
          });
        }
      });
    },
    logout: function logout() {
      var self = this;
      self.$store.commit(types.LOGOUT);
      window.location.href = '/logout/';
    },
    clickFullscreen: function clickFullscreen() {
      this.$parent.fullScreen();
    }
  }
};
