import $ from 'jquery';
import screenfull from 'screenfull';
import userConfig from '@/config';
import HeaderBox from '@/components/base/header';
import LeftNavBox from '@/components/base/left_nav';

String.prototype.endWith = function (endStr) {
  var d = this.length - endStr.length;
  return d >= 0 && this.lastIndexOf(endStr) == d;
};

export default {
  name: 'main_page',
  data: function data() {
    return {
      iframeSrc: ''
    };
  },
  created: function created() {},
  mounted: function mounted() {},
  methods: {
    iframeLoaded: function iframeLoaded() {},
    updateIframeSrc: function updateIframeSrc(src) {
      var self = this;

      if (src.endWith("pdf")) {
        src = '/static/js/pdfjs/web/viewer.html?file=' + src;
      }

      self.iframeSrc = src;
    },
    fullScreen: function fullScreen() {
      var self = this;

      if (screenfull.isEnabled) {
        if (self.$refs.mainIframe) {
          screenfull.request(self.$refs.mainIframe);
        }
      } else {
        alert('你的浏览器暂不支持全屏');
      }
    }
  },
  components: {
    HeaderBox: HeaderBox,
    LeftNavBox: LeftNavBox
  }
};
